import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CommonModule } from '@angular/common'; 
import { HttpClientModule } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { TonConnect, Wallet } from '@tonconnect/sdk';

interface UserData {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  telegramChatId: string;
  isTeacher: boolean;
  teacherNickname: string;
  description: string;
  rating: number;
  tags: string;
  message?: string;
}

@Component({
  selector: 'app-connect-wallet',
  templateUrl: './connect-wallet.component.html',
  styleUrls: ['./connect-wallet.component.css']
})
export class ConnectWalletComponent implements OnInit {
  userData: UserData | null = null;
  walletAddress: string | null = null;
  userId: string = '123';
  chatId: string | null = null;

  tonConnect = new TonConnect({
    manifestUrl: 'https://dmitryyyan.github.io/ton-wallet-manifest/tonconnect-manifest.json'
  });

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchChatId();

    // Підписка на зміни стану гаманця
    this.tonConnect.on('connect', (wallet: WalletInfo) => {
      console.log('Wallet connected:', wallet);
      this.walletAddress = wallet.account;  // Зберігаємо адресу гаманця
      this.saveWalletAddress();
    });

    this.tonConnect.on('disconnect', () => {
      console.log('Wallet disconnected');
      this.walletAddress = null;
    });
  }

  fetchChatId(): void {
    this.http.get<{ chatId: string }>('http://localhost:3000/api/getCurrentChatId').subscribe(
      response => {
        this.chatId = response.chatId;
        this.fetchUserData();
      },
      error => {
        console.error('Error fetching chat ID', error);
      }
    );
  }

  fetchUserData(): void {
    if (!this.chatId) {
      console.error('Chat ID is not defined');
      return;
    }
    this.http.get<UserData>(`http://localhost:5258/api/user/get-by-chat-id?chatId=${this.chatId}`).subscribe(
      data => {
        this.userId = data.id;
        this.userData = data;
      },
      error => {
        console.error('Error fetching user data', error);
      }
    );
  }

  async connectWallet() {
    try {
      // Викликаємо підключення TonConnect
      await this.tonConnect.connect();
      // Подія 'connect' обробиться вище і оновить walletAddress
    } catch (error) {
      console.error('Error connecting wallet:', error);
      alert('Не вдалося підключити гаманець');
    }
  }

  saveWalletAddress() {
    if (!this.walletAddress) {
      alert('Гаманець не підключено.');
      return;
    }

    const apiUrl = 'http://localhost:5258/api/wallet/set-by-user-id';
    const payload = {
      userId: this.userId,
      walletType: 'TonWallet',
      walletAddress: this.walletAddress,
    };

    this.http.post(apiUrl, payload).subscribe({
      next: (response) => {
        console.log('Гаманець успішно збережено:', response);
        alert('Гаманець успішно збережено!');
      },
      error: (error) => {
        console.error('Помилка збереження гаманця:', error);
        alert('Помилка збереження гаманця.');
      }
    });
  }
}
